mapper.select:
select user_num, user_id, user_pw, user_name, user_nick, user_phone, user_mail, USER_MENTO_CERT, user_monitor, user_agree, user_career
from tbl_user
where user_id = #{userId} and user_pw = #{userPw}

select user_num
from tbl_user
where user_id = #{userId} and user_name = #{userName} and user_phone = #{userPhone}

mapper.update:
UPDATE tbl_user
SET user_pw = #{userPw}
WHERE user_num = #{userNum}

mapper.select:
SELECT count (user_id)
FROM tbl_user
WHERE user_id = #{userId}

SELECT count (user_nick)
FROM tbl_user
WHERE user_nick = #{userNick}

SELECT count (user_mail)
FROM tbl_user
WHERE user_mail = #{userMail}
